/*global define: true*/

"use strict";

define(function () {

    return {

        helpers: ["config"],

        done: function (api) {
            api.done("Hello world.");
        },

        flush: function (api) {
            api.flush("Hello ");
            api.flush("world.");
            api.done();
        },

        error: function (api) {
            throw new Error("Hello error.");
        },

        use: function (api) {
            api.use.config.get("key");
            api.done("Hello config.");
        },

        tmplAbsolute: function (api) {
            api.done({key: "tmplAbsolute"}, {tmpl: {module: "api/tmpls", name: "tmpl"}});
        },

        tmplRelative: function (api) {
            api.done({key: "tmplRelative"}, "tmpl");
        },

        tmplMetaRelative: function (api) {
            api.done({key: "tmplMetaRelative"}, {tmpl: "tmpl"});
        },

        tmplInferred: function (api) {
            api.done({key: "tmplInferred"});
        },

        tmplJson: function (api) {
            api.done({key: "tmplJson"}, ".json");
        },

        tmplMetaJson: function (api) {
            api.done({key: "tmplMetaJson"}, {tmpl: ".json"});
        },

        tmplXml: function (api) {
            api.done({key: "tmplXml"}, ".xml");
        },

        tmplMetaXml: function (api) {
            api.done({key: "tmplMetaXml"}, {tmpl: ".xml"});
        },

        tmplRaw: function (api) {
            api.done({key: "tmplRaw"}, ".raw");
        },

        tmplMetaRaw: function (api) {
            api.done({key: "tmplMetaRaw"}, {tmpl: ".raw"});
        }
    };
});