/*jslint nomen: true*/

"use strict";

var requirejs = require('requirejs'),
    path = require("path");

/*
    @param options object

    options = {
        root: <path to app root>
    }
*/

module.exports = function (options) {

    var dirname = path.join(__dirname);

    /*
        Setup requirejs
    */

    requirejs.config({
        // Set the base of the app for RequireJS
        baseUrl: options.root,
        // Should these paths be generated per call?
        paths: {
            "lib/utils": dirname + "/utils",
            "lib/handlers/dispatcher": dirname + "/handlers/dispatcher",
            "lib/handlers/api": dirname + "/handlers/api",
            "lib/helpers/config": dirname + "/helpers/config",
            "lib/helpers/render": dirname + "/helpers/render",
            "lib/helpers/html": dirname + "/helpers/html",
            "lib/helpers/composite": dirname + "/helpers/composite",
            "lib/handlebars/base": dirname + "/handlebars/base"
        },
        nodeRequire: require
    });

    return requirejs;
};