/*jslint nomen: true*/

'use strict';

var program = require("commander"),
    connect = require("connect"),
    path = require("path"),
    dispatcher = require("../adapters/http");

program
    .command(path.basename(__filename, ".js"))
    .option("-r, --root [dir]", "which directory to run from", process.cwd())
    .option("-p, --port [port]", "which port to use", 3000)
    .description('start a local connect server for developing an online web application')
    .action(function (options) {
        connect()
            .use(connect.favicon()) // fav icon
            .use(connect.logger("dev")) // request logging
            .use(connect.static(options.root)) // user files
            .use("/lib", connect.static(path.join(__dirname, "/../"))) // framework files
            // If no static files were found do some processing
            .use(connect.query())
            // .use(connect.bodyParser()) // do this on demand not on every request
            .use(dispatcher(options)) // The main dispatch call
            .listen(options.port);

        console.log("Running at http://localhost:" + options.port + "/");
    });

// program.parse(process.argv);