/*global define: true*/

"use strict";

define(["lib/utils"], function (utils) {

    return {

        init: function (request, response, api) {
            this.api = api;
        },

        done: function (data, meta) {
            var self = this;
            this.execute(data, meta, function (edata, emeta) {
                self.api.done(edata, emeta);
            });
        },

        execute: function (data, meta, callback) {

            var html;

            // Simple test to see if we are on the client
            if (utils.runtime() === "client") {
                callback(data, meta);
                return;
            }

            html = "<!DOCTYPE html>";
            html += "<html>";
            html += "<head>";
            html += "<title>Ash ~ for the bohemian web developer</title>";
            html += "<script data-main=\"init\" src=\"./lib/require.js\"></script>";
            html += "</head>";
            html += "<body>";
            html += data;
            html += this.foo(meta);
            html += "</body>";
            html += "</html>";

            callback(html, meta);
        },

        foo: function (meta) {

            if (!meta) {
                meta = {};
            }
// TODO: I'm here
console.log(meta);
            return "<script></script>";
        }
    };
});