/*jslint nomen: true*/

/*global describe: true, it: true*/

"use strict";

var assert = require("assert"),
    root = require("path").join(__dirname, "/../examples/"),
    dispatch = require("../lib/adapters/func")({root: root});

describe("helper html", function () {

    describe("api.use.html", function () {
        it("should return <html/>", function () {
            var count = 0;
            dispatch({module: "helpers/html/ctrl", method: "index"}, function (data, meta) {
                if (count === 0) {
                    assert.equal(/body/.test(data), true);
                }
                count = count + 1;
            });
        });
    });
});
